// First Versionif (balance >= 0){   if (INTEREST_RATE >= 0)      balance = balance + (INTEREST_RATE * balance) / 12;}else   balance = balance - OVERDRAWN_PENALTY;  // Second Versionif (balance >= 0)   if (INTEREST_RATE >= 0)      balance = balance + (INTEREST_RATE * balance) / 12;else   balance = balance - OVERDRAWN_PENALTY;  // Equivalent to Second Versionif (balance >= 0){   if (INTEREST_RATE >= 0)      balance = balance + (INTEREST_RATE * balance) / 12;   else      balance = balance - OVERDRAWN_PENALTY;}