String seatLocationCode;// < Code here assigns a value to seatLocationCode >// . . . double price = -0.01;if (seatLocationCode != null){   switch (seatLocationCode)   {      case "balcony":         System.out.println("Balcony.");         price = 15.00;         break;      case "mezzanine":         System.out.println("Mezzanine.");         price = 30.00;         break;      case "orchestra":         System.out.println("Orchestra.");         price = 40.00;         break;      default:         System.out.println("Unknown ticket code.");         break;   } // end switch} // end if