Scanner keyboard = new Scanner(System.in);System.out.print("What is his age? ");int hisAge = keyboard.nextInt();System.out.print("What is her age? ");Integer herAge = keyboard.nextInt();Integer ageDifference = Math.abs(hisAge - herAge);System.out.println("He is " + hisAge + ", she is " + herAge +                   ": a difference of " + ageDifference + ".");                                      