   // Removes and returns the entry at a given index within the array.   // If no such entry exists, returns null.	private T removeEntry(int givenIndex)	{		T result = null;      		if (!isEmpty() && (givenIndex >= 0))		{         result = bag[givenIndex];               // entry to remove         numberOfEntries--;         bag[givenIndex] = bag[numberOfEntries]; // replace entry to remove with last entry         bag[numberOfEntries] = null;            // remove reference to last entry		} // end if            return result;	} // end removeEntry   