// Returns a reference to the node at a given position.// Precondition: The chain is not empty;// 1 <= givenPosition <= numberOfNodes	private Node getNodeAt(int givenPosition){   assert !isEmpty() && (1 <= givenPosition) && (givenPosition <= numberOfNodes);   Node currentNode = firstNode;      // traverse the list to locate the desired node   for (int counter = 1; counter < givenPosition; counter++)      currentNode = currentNode.getNextNode();      assert currentNode != null;      return currentNode;} // end getNodeAt